pragma solidity ^0.4.21;


import "./token/MintableToken.sol";

import "./token/PausableToken.sol";
import "./fundraiser/MintableTokenFundraiser.sol";
import "./fundraiser/IndividualCapsFundraiser.sol";
import "./fundraiser/GasPriceLimitFundraiser.sol";

import "./fundraiser/ForwardFundsFundraiser.sol";


/**
 * @title hcmcoinToken
 */

contract hcmcoinToken is MintableToken, PausableToken {
  constructor(address _owner, address _minter)
    StandardToken(
      "hcmcoin",   // Token name
      "HCM", // Token symbol
      18  // Token decimals
    )
    HasOwner(_owner)
    MintableToken(_minter)
    public
  {
  }
}



/**
 * @title hcmcoinTokenFundraiser
 */

contract hcmcoinTokenFundraiser is MintableTokenFundraiser, IndividualCapsFundraiser, ForwardFundsFundraiser, GasPriceLimitFundraiser {


  constructor()
    HasOwner(msg.sender)
    public
  {
    token = new hcmcoinToken(
      msg.sender,  // Owner
      address(this)  // The fundraiser is the minter
    );



    initializeBasicFundraiser(
      1529535540, // Start date = 20 Jun 2018 22:59 UTC
      1532088000,  // End date = 20 Jul 2018 12:00 UTC
      3330, // Conversion rate = 3330 HCM per 1 ether
      0xeeaA46073964D99B2C95F6F86eD7Fd5730F02820     // Beneficiary
    );

    initializeIndividualCapsFundraiser(
      (0.1 ether), // Minimum contribution
      (5 ether)  // Maximum individual cap
    );

    initializeGasPriceLimitFundraiser(
        50000000000 // Gas price limit in wei
    );








  }

}
